INSERT INTO `courses` VALUES (168,'Жаңа ниша пакеті. Сату маманы','INCON ниша',349770,'<p style=\"text-align: left;\"><span style=\"color: #ffffff;\">Барлықтарыңызғв сәлем!&nbsp;Сіздермен&nbsp;бірге Жұлдыз Ақболат. \"FOCUS\"&nbsp; курсына қош келдіңіз!</span></p>\r\n<p style=\"text-align: left;\"><span style=\"color: #ffffff;\">\"FOCUS\"&nbsp; курсының \"ЖАҢА НИША. Сату маманы\" пакетінде сіз нарықтағы ең сұранысқа ие мамандықты игеріп, сол бойынша өзіңіздің қызметіңізді шығынсыз сатуды үйренесіз.</span></p>\r\n<p style=\"text-align: left;\"><span style=\"color: #ffffff;\">Курс бағдарламасы:</span></p>\r\n<p style=\"text-align: left;\"><span style=\"color: #ffffff;\">I МОДУЛЬ: Ақшаға дайындық</span></p>\r\n<p style=\"text-align: left;\"><span style=\"color: #ffffff;\">II МОДУЛЬ: Сату маманы мамандығын игеру</span></p>\r\n<p style=\"text-align: left;\"><span style=\"color: #ffffff;\">III&nbsp;МОДУЛЬ: Стратегия</span></p>\r\n<p style=\"text-align: left;\"><span style=\"color: #ffffff;\">IV&nbsp;МОДУЛЬ: Анализ ЦА</span></p>\r\n<p style=\"text-align: left;\"><span style=\"color: #ffffff;\">V&nbsp;МОДУЛЬ: Распаковка личности. Упаковка</span></p>\r\n<p style=\"text-align: left;\"><span style=\"color: #ffffff;\">VI&nbsp;МОДУЛЬ: Клиентпен жұмыс</span></p>\r\n<p style=\"text-align: left;\"><span style=\"color: #ffffff;\">VII&nbsp;МОДУЛЬ: Қызмет прогреві</span></p>\r\n<p style=\"text-align: left;\"><span style=\"color: #ffffff;\">VIII&nbsp;МОДУЛЬ: Продажа</span></p>','uploads/1639898061wDtQh.JPG','2023-12-19 07:14:00',0,'2021-12-10 18:35:53','2021-12-21 09:19:37'),(169,'Жаңа ниша пакеті. DIRECT мастер','INCON ниша',349770,'<p style=\"text-align: left;\"><span style=\"color: #ffffff;\">Барлықтарыңызғв сәлем!&nbsp;Сіздермен&nbsp;бірге Жұлдыз Ақболат. \"FOCUS\"&nbsp; курсына қош келдіңіз!</span></p>\r\n<p style=\"text-align: left;\"><span style=\"color: #ffffff;\">\"FOCUS\"&nbsp; курсының \"ЖАҢА НИША. DIRECT МАСТЕР\" пакетінде сіз нарықтағы ең сұранысқа ие мамандықты игеріп, сол